mapper.update:
UPDATE post
SET category_id = #{categoryId},
    subject     = #{subject},
    content     = #{content},
    pinned      = #{pinned}
WHERE id = #{id}

mapper.select:
SELECT COUNT(*)
FROM post p
JOIN category c ON p.category_id = c.id
WHERE p.board_id = #{boardId}


    AND c.id = #{categoryId}



    AND Date(p.create_at) >= #{startDate}



    AND Date(p.create_at) <= #{endDate}



    AND (
    p.subject LIKE CONCAT('%', #{keyword}, '%')
    OR p.content LIKE CONCAT('%', #{keyword}, '%')
    )

SELECT p.id, c.name AS categoryName, p.subject, m.name AS writerName, p.pinned AS pinned,
-- 썸네일 파일명 (예: 대표 첨부파일 1개만) ( SELECT pf.saved_filename FROM post_file pf WHERE pf.post_id = p.id ORDER BY pf.id ASC LIMIT 1 ) AS thumbnailFilename,
-- 댓글 수 ( SELECT COUNT(*) FROM comment WHERE post_id = p.id ) AS commentCount,
-- 첨부파일 수 ( SELECT COUNT(*) FROM post_file pf WHERE pf.post_id = p.id ) AS fileCount,
p.view_count AS viewCount, p.create_at AS createAt
FROM post p JOIN category c ON p.category_id = c.id JOIN member m ON p.member_id = m.id WHERE p.board_id = #{dto.boardId}
AND p.category_id = #{dto.categoryId}
AND DATE(p.create_at) >= #{dto.startDate}
AND DATE(p.create_at) <= #{dto.endDate}
AND ( p.subject LIKE CONCAT('%', #{dto.keyword}, '%') OR p.content LIKE CONCAT('%', #{dto.keyword}, '%') )
ORDER BY
p.create_at c.name p.subject p.view_count p.create_at
ASC DESC
, p.id DESC
LIMIT #{dto.size} OFFSET #{offset}

mapper.update:
UPDATE post
SET view_count = view_count + 1
WHERE id = #{postId}
